            }
            &-offer
            {
                font-size: 1.6rem;
                font-weight: 400;
                @include respond(tab-port){font-size: 1.4rem;}
            }
            &:not(:last-of-type)
            {
                border-bottom: 1px solid $color-primary;
            }
        }
        &--image-container
        {
            width: 8rem;
            height: 9rem;
            margin-right: 2rem;
            @include respond(tab-port){width: 6rem; height: 7rem;}
        }
        &--image
        {
            width: 100%;
            height: 100%;
            border-radius: 50%;
        }
    }
}

@keyframes moveDown
{
    0%
    {
        transform: translateY(-10rem);
    }
    100%
    {
        transform: translateY(0);
    }
}

