        display: flex;
        gap: 4rem;
        margin-bottom: 5rem;

        @include respond(tab-port)
        {
            flex-wrap: wrap;
            text-align: center;
        }
    }
    &__images
    {
        @include respond(tab-port){flex: 0 1 100%;}
    }
    &__image--big
    {
        height: 45rem;
        width: 60rem;
        margin-bottom: 3rem;


        @include respond(tab-land){ width: 50rem;}

        @include respond(phone){width: 35rem;height: 30rem;}
    }
    &__images--small-container
    {
        display: flex;
        align-items: center;
        gap: 3rem;

        @include respond(tab-land){gap: 2.5rem;}
        @include respond(tab-port){justify-content: center;}
        // @include respond(phone){gap: 1.5rem;}
    }
    &__image--small
    {
        width: 18rem;
        height: 15rem;

        @include respond(tab-land){width: 15rem;}
        @include respond(phone){width: 10rem;}
    }
    &__details
    {
        flex-basis: 40%;

        @include respond(tab-port){flex-basis: 100%;}
    }
    &__title
    {
        font-size: 2.8rem;
        font-weight: 500;
        margin-bottom: 1.3rem;
    }
    &__price
    {
        margin: 1.3rem 0;
        font-size: 2.6rem;
        font-weight: 600;
        color: $color-primary;
    }
    &__paragraph
    {
        margin: 1.3rem 0;
        font-size: 1.4rem;

        @include respond(tab-land){font-size: 1.3rem;}
    }
    &__select--header
    {
        font-size: 1.4rem;
        margin-bottom: 1.3rem;
    }
    &__colors
    {
        display: flex;
        margin-bottom: 1.3rem;

        @include respond(tab-port){justify-content: center;}
    }
    &__color
    {
        padding: 1.2rem;
        box-shadow: 0 1rem 3rem rgba($color-black, .2);
        border-radius: 3px;
        display: inline-block;
        cursor: pointer;

        &:not(:last-of-type){margin-right: 1.5rem;}

        &--1{background-color: #ff5329}
        &--2{background-color: #c7a98c}
        &--3{background-color: #5bcc25}
        &--4{background-color: #3e8ed5}
        &--5{background-color: #ff2180}
    }
    &__quantity
    {
        font-size: 1.9rem;
        margin-bottom: 1.3rem;

        display: flex;
        align-items: center;

        @include respond(tab-port){justify-content: center;}

        &>*
        {
            border: 1px solid $color-gray-light-3;
            padding: .1rem 2rem;
        }
    }
    &__minus
    {
        cursor: pointer;
        transition: all .2s ease-in-out;
    }
    &__plus
    {
        cursor: pointer;
        transition: all .2s ease-in-out;
    }
    &__sizes
    {
        margin-bottom: 1.3rem;

        display: flex;

        @include respond(tab-port){justify-content: center;}
    }
    &__size
    {
        &:not(:last-of-type){margin-right: 1.5rem;}
        border: 1px solid $color-gray-light-3;
        padding: .7rem 1.7rem;
        cursor: pointer;
        transition: all .2s ease-in-out; // for selecting the color in javascript

        &:hover
        {
            color: $color-white;
            background: $color-primary;
        }
        &:active, &:visited
        {
            color: $color-white;
            background: $color-primary-dark;
        }
    }

    &__free--shipping
    {
        text-decoration: underline;
        cursor: pointer;
    }
}
.product__main
{
    .product__cart__like
    {
        justify-content: flex-start;

        @include respond(tab-port){justify-content: center;}

        .btn
        {
            padding: .5rem 10rem;
            margin-right: 2rem;
            @include respond(tab-land){padding: .5rem 5rem;}
        }
    }
}














// Reviews and Description
.product
{
    // Description and Reviews
    &__desc__reviews
    {
        display: flex;
        justify-content: center;
        font-size: 1.4rem;
        font-weight: 700;
        margin-bottom: 2rem;

        & > * {cursor: pointer;}

        @include respond(tab-port)
        {
            font-size: 1.2rem;
        }

    }
    &__desc--header
    {
        margin-right: 8rem; 
        transition: all .2s ease-in-out;
        color: $color-primary;

        @include respond(tab-port)
        {
            margin-right: 5rem;   
        }
    }
    &__reviews--header{ transition: all .2s ease-in-out;}
    &__desc--detailed
    {
        font-size: 1.6rem;
        margin-bottom: 2rem;

        @include respond(tab-port)
        {
            font-size: 1.4rem;
            margin-bottom: 1.5rem;   
        }
    }
    &__reviews
    {
        margin-top: 2rem;
        display: none;

        &--all
        {
            font-size: 2.2rem;
            font-weight: 700;
            margin: 4rem 0;

            @include respond(tab-port)
            {
                font-size: 2rem;   
                margin: 3rem 0;
            }
        }

        &--review
        {
            display: flex;
            align-items: center;

            margin-bottom: 4rem;
            margin-top: 4rem;

            @include respond(tab-port)
            {
                margin-bottom: 3rem;
                margin-top: 3rem;   
            }

            @include respond(phone){flex-direction: column; text-align: center;}

            &-img
            {
                width: 10rem;
                height: 10rem;
                border-radius: 50%;
                margin-right: 4rem;

                @include respond(tab-port)
                {
                    margin-right: 2rem;
                }
                @include respond(phone){margin-bottom: 2rem;}
            }
            &-name
            {
                display: flex;
                flex-direction: column;

                &-name
                {
                    margin-bottom: 1rem;
                    font-size: 1.6rem;
                }
            }

            &-paragraph
            {
                flex: 0 1 60%;
                margin-left: auto;
                font-size: 1.6rem;

                @include respond(tab-port)
                {
                    font-size: 1.4rem;
                }
            }
        }
        &--date
        {
            text-align: right;
            font-size: 1.6rem;
            margin-bottom: 4rem;

            @include respond(tab-port)
            {
                font-size: 1.4rem;
                margin-bottom: 3rem;   
            }
        }
    }

    //Related Products
    &__related
    {
        margin-top: 4rem;

        @include respond(tab-port)
        {
            margin-top: 3rem;   
        }
        &--header
        {
            text-align: center;
            font-size: 2.2rem;
            font-weight: 700;
            color: $color-primary;
            margin-bottom: 4rem;

            @include respond(tab-port)
            {
                font-size: 2rem;
                margin-bottom: 3rem;   
            }
        }
        .col-1-of-4
        {
            height: 34rem;

            @include respond(tab-port)
            {
                height: 27rem;
            }
        }
        .row{padding: 0 !important;}
    }
}
