.form
{
    display: flex;
    flex-direction: column;

    &__header
    {
        font-size: 5.5rem;
        font-weight: 700;
        margin-bottom: 2rem;

        @include respond(tab-port)
        {
            font-size: 4rem;
            text-align: center;
        }
    }


    // Radio group
    &__group:not(:last-child)
    {
        margin-bottom: .5rem;
    }
    &__group--radio-first-input
    {
        display: flex;
        align-items: center;
        width: 50rem;
        margin-bottom: 3rem !important;

        @include respond(tab-port)
        {
            flex-wrap: wrap;
            margin-bottom: 2rem !important;
        }
    }
    &__radio--group
    {
        flex: 0 1 49%;

        @include respond(tab-port)
        {
            flex: 0 1 100%;
            margin-bottom: 1.6rem;
        }
    }   
    &__radio--input
    {
        display: none;
    }
    &__radio--label
    {
        cursor: pointer;
        font-size: 1.6rem;
        padding-left: 4.5rem;
        position: relative;

    }
    &__radio--button
    {
        height: 3rem;
        width: 3rem;
        border: 5px solid $color-primary;
        border-radius: 50%;
        display: inline-block;
        position: absolute;
        left: 0;
        top: -.3rem;

        &::after
        {
            content: "";
            height: 1.5rem;
            width: 1.5rem;
            display: block;
            border-radius: 50%;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background-color: $color-primary;
            opacity: 0;
            transition: all .3s;
        }
    }

    &__radio--input:checked ~ &__radio--label &__radio--button::after
    {
        opacity: 1;
    }

    // Inputs
    &__input
    {
        font-size: 1.5rem;
        padding: 1.5rem 2rem;
        color: inherit;
        font-family: inherit;
        border-radius: 4rem;
        background-color: rgba($color-white, .5);
        border: 1px solid $color-gray-light-3;
        // border-bottom: 3px solid transparent;
        display: block;
        width: 90%;
        transition: all .3s;


        @include respond(tab-port)
        {
            width: 100%;
        }

        &:focus
        {
            outline: none;
            box-shadow: 0 1rem 2rem rgba($color-black, .1);
            border-bottom: 3px solid $color-secondary-light;
        }
        &:focus:invalid // when you focus but text is empty or wrong
        {
            border-bottom: 3px solid $color-primary
        }
        &::-webkit-input-placeholder
        {
            color: $color-gray-dark-2; 
        }
    }


    &__label
    {
        font-size: 1.2rem;
        font-weight: 700;
        margin-left: 2rem;
        margin-top: .7rem;
        display: block;
        transition: all .3s;
    }

    &__input:placeholder-shown + &__label
    {   
        opacity: 0;
        visibility: hidden;
        transform: translateY(-4rem);
    }

    // buttons

    &__button
    {
        cursor: pointer;
        font-size: 1.5rem;
        padding: 1.5rem 2rem;
        width: 90%;
        border-radius: 4rem;
        margin-bottom: 2rem;

        @include respond(tab-port)
        {
            width: 100%;   
        }
    }

    &__forget
    {
        width: 90%;
        text-align: right;
        text-decoration: none;
        font-size: 1.4rem;
        font-weight: 700;   

        @include respond(tab-port)
        {   
            width: 100%;
        }
    }

    &__links--container
    {
        display: flex;
        justify-content: center;
        align-items: center;
        width: 90%;

        & > *:not(:last-child)
        {
            margin-right: 5rem;
        }

        @include respond(tab-port)
        {
            width: 100%;   
        }
    }
    &__links--href
    {

        font-size: 2.5rem;
        &--1{color: #4398e7e3;}
        &--2{color:  #1759ca;}
        &--3{color: $color-primary;}
    }

    &__already
    {
        margin-top: 2rem;
        font-size: 1.3rem;
        font-weight: 700;
        text-align: center;
        width: 90%;

        &--log
        {
            text-decoration: none;
        }

        @include respond(tab-port)
        {   
            width: 100%;
        }
    }
}

