// I changed the top cat in the main page to be normal photos instead of the slider
// i leave this page in case i needed it later
.slider
{
    overflow: hidden;
    position: relative;
    height: 90vh;
    margin-bottom: 2rem !important;

    @include respond(tab-port){height: 50vh;}
    @include respond(phone){height: 45vh;}
}
.slide
{
    text-align: center;
    position: absolute;
    top: 0;
    width: 100%;
    transition: all .2s ease;

    &__image
    {
        width: 60%;
        height: 60rem;

        @include respond(tab-port){height: 40rem; width: 50%;}
        @include respond(phone){width: 70%;}
    }
    &__image:hover + &__cat{background: $color-gray-dark-2;}
    &__cat
    {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        padding: 3rem 8rem;
        border-radius: 1rem;
        background: rgba($color-gray-dark-2, .3);
        color: $color-white;
        font-size: 1.6rem;
        font-weight: 400;
        letter-spacing: 1px;
        cursor: pointer;
        transition: all .2s ease-in-out;

        &:hover{background: $color-gray-dark-2;}

