            position: absolute;
            top: 9rem;
            left: 0;
            display: block;
            background: $color-white;
            width: 100%;
            // box-shadow: 0 1rem 3rem rgba($color-black, .9);
            clip-path: polygon(0 0, 100% 0, 100% 0, 0 0);
